class Modal{constructor(e){this.$el=document.querySelector(e),this.$el.style.display="none",this.boundHide=e=>this.hide(e),this.$el.addEventListener("click",this.boundHide),this.$elForm=this.$el.querySelector(".innerModal")}hide(e){e.target===this.$el&&(this.$el.style.display="none")}show(){this.$el.style.display="flex"}}const login=new Modal("#login");document.querySelector(".login_btn").addEventListener("click",()=>login.show());